contracts/mocks/VRF/VRFV2WrapperMock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

import "@chainlink/contracts/src/v0.8/interfaces/VRFV2WrapperInterface.sol";
import "@chainlink/contracts/src/v0.8/mocks/VRFCoordinatorV2Mock.sol";
import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";

import "hardhat/console.sol";

error notSender();

contract VRFV2WrapperMock is VRFV2WrapperInterface {
    address LINK;
    VRFCoordinatorV2Mock COORDINATOR;
    uint64 subId;
    address sender;
    uint256 lastRequestId_;

    constructor(address link, address, address coordinator) {
        LINK = link;
        COORDINATOR = VRFCoordinatorV2Mock(coordinator);
        subId = COORDINATOR.createSubscription();
    }

    function calculateRequestPrice(uint32) external view returns (uint256) {
        console.log("calculateRequestPrice", address(this));
        return 2000000;
    }

    function onTokenTransfer(
        address _sender,
        uint256 _amount,
        bytes calldata _data
    ) external {
        require(msg.sender == LINK, "only callable from LINK");

        sender = _sender;

        (
            uint32 callbackGasLimit,
            uint16 requestConfirmations,
            uint32 numWords
        ) = abi.decode(_data, (uint32, uint16, uint32));
        COORDINATOR.fundSubscription(subId, uint96(_amount));
        uint256 requestId = COORDINATOR.requestRandomWords(
            bytes32("test"),
            subId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        lastRequestId_ = requestId;
        COORDINATOR.fulfillRandomWords(requestId, address(this));
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal {
        if (sender == address(0)) revert notSender();
        VRFV2WrapperConsumerBase(sender).rawFulfillRandomWords(
            _requestId,
            _randomWords
        );
    }

    function lastRequestId() external view override returns (uint256) {
        return lastRequestId_;
    }

    function estimateRequestPrice(
        uint32,
        uint256
    ) external pure returns (uint256) {
        return 2000000;
    }
}

