// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";
import "./SawpEthToLink.sol";

import "hardhat/console.sol";

error RandomNumber__insufficientFunds();

abstract contract RandomNumber is VRFV2WrapperConsumerBase, SwapEthToLink {
    uint32 constant callbackGasLimit = 100000;

    constructor(
        address linkAdderess,
        address wrapperAddress,
        address uniswapRouterAddress
    )
        VRFV2WrapperConsumerBase(linkAdderess, wrapperAddress)
        SwapEthToLink(uniswapRouterAddress, linkAdderess)
    {}

    function requestRandomWords() internal returns (uint256 requestID) {
        uint256 price = VRF_V2_WRAPPER.calculateRequestPrice(callbackGasLimit);
        uint256 linkToGet;
        if (price < s_linkBalance) {
            linkToGet = 0;
        } else {
            linkToGet = price - s_linkBalance;
        }

        convertEthToLink(linkToGet);

        if (s_linkBalance < price) revert RandomNumber__insufficientFunds();
        return requestRandomness(callbackGasLimit, 1, 1);
    }
}

contracts/SawpEthToLink.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./interfaces/uniswapRouter/IUniswapV2Router02.sol";

error SwapEthToLink__insufficientFunds(
    uint256 balance,
    uint256 necessaryAmount
);

contract SwapEthToLink {
    IUniswapV2Router02 uniswapRouter;
    address private immutable i_LinkAddress;

    uint256 internal s_linkBalance = 0;

    constructor(address uniswapRouterAddress, address linkAddress) {
        uniswapRouter = IUniswapV2Router02(uniswapRouterAddress);
        i_LinkAddress = linkAddress;
    }

    event Converted(
        uint256 needAmount,
        uint256 linkReceived,
        uint256 ethSpended
    );

    function convertEthToLink(
        uint256 linkAmount
    ) internal returns (uint256 amount) {
        uint256 deadline = block.timestamp + 30;
        uint256 ethPerLink = getEthForLinkEstimation(1);
        uint256 ethToSwap = linkAmount * ethPerLink;
        if (ethToSwap > address(this).balance) {
            revert SwapEthToLink__insufficientFunds(
                address(this).balance,
                ethToSwap
            );
        }

        uint256 resultAmount = uniswapRouter.swapETHForExactTokens{
            value: ethToSwap
        }(linkAmount, getPathForEthToLink(), address(this), deadline)[1];
        s_linkBalance += resultAmount;
        emit Converted(linkAmount, resultAmount, ethToSwap);
        return resultAmount;
    }

    function getEthForLinkEstimation(
        uint256 linkAmount
    ) private view returns (uint256) {
        return uniswapRouter.getAmountsIn(linkAmount, getPathForEthToLink())[0];
    }

    function getPathForEthToLink() private view returns (address[] memory) {
        address[] memory path = new address[](2);
        path[0] = uniswapRouter.WETH();
        path[1] = i_LinkAddress;

        return path;
    }

    function spendLink(uint256 linkToSpend) internal {
        s_linkBalance -= linkToSpend;
    }
}

